import React from 'react';
import './Hero.css';
import hand_icon from '../assets/logo.png'

export const Hero = () => {
  return (  
    <div className='hero'>
    <div className="hero-left">
        <h2>New Arrivals Only</h2>
        <div>
        <div className="hand-icon">
            <p>New</p>
            <img src={hand_icon} alt="" />
        </div>
        <p>Collections</p>
        <p>For Everyone</p>
        </div>

    </div>
    </div>
  )
}
