import React, { createContext, useState } from "react";
import all_product from "../components/assets/all_product";


export const ShopContext = createContext(null);

export const getDefaultCart = ()=>{
    let cart = {};
    for(let i=0;i<all_product.length;i++){
       cart[i]=0;
    }
   return cart;
   }

export const ShopContextProvider = (props) =>{
    const [cartItems,setCartItems] = useState(getDefaultCart());
   
    const addToCart=(itemId,quantity)=>{
        setCartItems((prev)=>({...prev,[itemId]:prev[itemId]+quantity}))
        
    }
    const removeFromCart=(itemId)=>{
        setCartItems((prev)=>({...prev,[itemId]:prev[itemId]-1}))
    }

    const getTotalCartAmount=()=>{
        let totalAmount = 6;
        for(let item=0;item<all_product.length;item++){
             console.log("items:",cartItems[item]) 
            if(cartItems[item]!=(0)){
                console.log("inside if staement")
                let itemInfo = all_product.find((product)=>(product.id) === Number(item))/* .find((product)=>(product.id) === Number(item)) */;
                console.log("iteminfo",itemInfo)
                    totalAmount +=  itemInfo.new_price * cartItems[item] ;
            }
            else{
                totalAmount=18;
            }
        return totalAmount;
        }   
        }
    
    const contextValue = {all_product,cartItems,setCartItems,addToCart,removeFromCart,getTotalCartAmount};
    
    return (<ShopContext.Provider value={contextValue}>
            {/* props.children are all the elements that are going to use the contextValue
            and is not the contextValue */}
            {props.children}
        </ShopContext.Provider>)
}
